		v--;u--;
		ed[i][0] = v;
		ed[i][1] = u;
		g[v].push_back(i);
		g[u].push_back(i);
	}
	k = 0;
	for (int i = 0; i < n; i++)
		h[i] = -1;
	h[root] = 0;
	dfs1(root);
	/*
	for (int i = 0; i < m; i++)
		eprintf("%d", (int)isBridge[i]);
	eprintf("\n");
	*/
	for (int v = 0; v < n; v++)
		myComp[v] = -1;
	for (int v = 0; v < n; v++) {
		if (myComp[v] != -1) continue;
		dfsComp(v);
		k++;
	}
	for (int i = 0; i < m; i++) {
		if (!isBridge[i]) continue;
		int v = myComp[ed[i][0]], u = myComp[ed[i][1]];
		assert(v != u);
		G[v].push_back(mp(u, ed[i][2]));
		G[u].push_back(mp(v, ed[i][2]));
	}

	if (k == 1) {
		for (int i = 0; i < n; i++)
			printf("%lld ", val[0]);
		printf("\n");
		return 0;
	}

	for (int v = 0; v < k; v++) {
		deg[v] = (int)G[v].size();
		if (deg[v] <= 1) q[topQ++] = v;
	}
	for (int i = 0; i < topQ; i++) {
		solve(q[i]);
	}

	for (int v = 0; v < k; v++) {
		ans[v] = val[v];
		for (pii e : G[v]) {
			int u = e.first;
			ans[v] += dp[mp(v, u)];
		}
	}
	for (int i = 0; i < n; i++)
		printf("%lld ", ans[myComp[i]]);
	printf("\n");

	return 0;
}
