void dfs(int u,int p) {
    int v=0;h[u]=h[p]+1;s[u]=1;P[u][0]=p;
    FOR(i,0,Sz(g[u])-1) {
        v=g[u][i];if (v==p) continue;
        dfs(v,u);s[u]+=s[v];
        sub[u]+=sub[v]+s[v];
