    }
}

int LCA(int u,int v) {
    if (h[u]<h[v]) swap(u,v);
    FORD(i,17,0) if (h[P[u][i]]>=h[v]) u=P[u][i];
    if (u==v) return u;
    FORD(i,17,0) if (P[u][i]!=P[v][i]) u=P[u][i],v=P[v][i];
    return P[u][0];
}

int dis(int u,int v,int p) {return h[u]+h[v]-2*h[p];}

int child(int u,int p) {
    FORD(i,17,0) if (h[P[u][i]]>h[p]) u=P[u][i];
    return u;
}

double query(int u,int v) {
    int64 sum,num;int p=LCA(u,v),ch;
    if (h[u]<h[v]) swap(u,v);
    if (p!=v) {
        num=1LL*s[u]*s[v];
        sum=sub[u]*s[v]+sub[v]*s[u]+1LL*num*(dis(u,v,p)+1);
    } else {
        ch=child(u,v);
        num=1LL*s[u]*(n-s[ch]);
        sum=sub[u]*(n-s[ch])+(all[v]-sub[ch]-s[ch])*s[u]+1LL*num*(dis(u,v,p)+1);
    }
    return 1.0*sum/num;
}

