using ll = long long;

void solve() {
    int n;
    cin >> n;
    string a, b;
    cin >> a >> b;
    if (a == b) {
        cout << "0\n";
        return;
    }
    if (a == string(n, '0') || b == string(n, '0')) {
        cout << "-1\n";
        return;
    }
    vector<int> ans;
    auto Op = [&](int k) {
        if (k > 0) {
            for (int i = 0; i + k < n; ++i) {
                a[i] ^= a[i + k] ^ '0';
            }
        } else {
            for (int i = n - 1; i + k >= 0; --i) {
                a[i] ^= a[i + k] ^ '0';
            }
        }
        ans.push_back(k);
    };
    for (int i = 0; i < n; ++i) {
        if (a[i] == b[i]) continue;
        int bit = 0;
        while (bit < n && a[bit] == '0') bit++;
        if (i == bit) continue;
        assert(bit != n);
        Op(bit - i);
        assert(a[i] == b[i]);
    }
    for (int i = n - 1; i >= 0; --i) {
        if (a[i] == b[i]) continue;
        int bit = n - 1;
        while (bit >= 0 && a[bit] == '0') bit--;
        assert(i != bit);
        Op(bit - i);
        assert(a[i] == b[i]);
    }
    int diff = 0;
    for (int i = 0; i < n; ++i) diff += (a[i] != b[i]);
    assert(diff == 0);
    cout << ans.size() << '\n';
    for (auto &x: ans) cout << x << ' ';
    cout << '\n';
}

