inline LL getint()
{
	register LL num=0;
	register char ch;
	do ch=getchar(); while (ch<'0' || ch>'9');
	do num=num*10+ch-'0', ch=getchar(); while (ch>='0' && ch<='9');
	return num;
}
inline void putint(int num)
{
	char stack[11];
	register int top=0;
	if (num==0) stack[top=1]='0';
	for ( ;num;num/=10) stack[++top]=num%10+'0';
	for ( ;top;top--) putchar(stack[top]);
	putchar('\n');
}
int solve(LL x, LL y)
{
	if (!x && !y) return 1;
	if (!x || !y) return 0;
	if (s.count(node(x, y))) return s[node(x, y)];
