vi path_to(int a, int b) {
	vi st, en;
	while (a != b) {
		if (depth[a] > depth[b]) {
			st.pb(a);
			a = par[a];
		} else {
			en.pb(b);
			b = par[b];
		}
	}
	st.pb(b);
	reverse(all(en));
	st.ins(end(st),all(en));
	return st;
}

void solve_subtree(int x, int p) {
	assert(marked.at(p));
	auto deal_path = [&](int y, int z) {
		// dbg("DEAL PATH",y,z);
		vi path = path_to(y,x);
		int ind = 0;
		while (path.at(ind) != z) ++ind;
		x = path.at(ind);
		if (ind) marked[path.at(ind-1)] = 1;
		if (ind+1 < sz(path)) marked[path.at(ind+1)] = 1;
	};
	{
		int y = get_opt_query(x,p);
		int z = query(y); if (z == p) return;
		deal_path(y,z);
	}
	while (1) {
		assert(!marked.at(x));
		vpi cands;
		{
			vpi queries = get_adj_queries(x);
			each(t,queries) if (!marked.at(t.f)) cands.pb(t);
