		bool found = 0;
		each(t,cands) {
			int y = t.s; int z = query(y);
			if (z == x) continue;
			deal_path(y,z);
			found = 1;
			break;
		}
		if (found) continue;
		endpoints.pb(x); return;
	}
}

void solve(int f) {
	vpi v = get_adj_dp(f);
	each(t,v) {
		solve_subtree(t.s,f);
		if (sz(endpoints) == 2) break;
	}
	while (sz(endpoints) < 2) endpoints.pb(f);
	ps("!",endpoints[0],endpoints[1]);
}

