['#include <cstdio>
#include <cstdlib>
#include <algorithm>

typedef long long int64;

struct Event
{
	int floor, idx, target;
	friend bool operator == (Event a, Event b)
	{
		return a.idx == b.idx && a.floor == b.floor;
	}
	friend bool operator < (Event a, Event b)
	{
		return a.floor < b.floor || a.floor == b.floor && a.idx < b.idx;
	}
	static Event Null()
	{
		Event ret;
		ret.idx = -1;
		return ret;
	}
	Event(){}
	Event(int a, int b, int c)
	{
		floor = a, idx = b, target = c;
	}
};

struct NODE{Event Val; int Pry, Size; struct NODE *L, *R;};
typedef struct NODE *TNODE;

TNODE Head, Null;

inline void Update(TNODE &Root){Root->Size= 1 + Root->L->Size + Root->R->Size;}

inline void RotateLeft(TNODE &Root)
{
    TNODE Temp= Root->L;
    Root->L= Temp->R;
    Temp->R= Root;
    Update(Root); Update(Temp);
    Root= Temp;
}

inline void RotateRight(TNODE &Root)
{
    TNODE Temp= Root->R;
    Root->R= Temp->L;
    Temp->L= Root;
    Update(Root); Update(Temp);
    Root= Temp;
}

void Insert(TNODE &Root, Event Val)
{
    if(Root == Null)
    {
        Root= new(NODE);
        Root->Val= Val;
        Root->Size= 1;
        Root->Pry= rand();
        Root->L= Root->R= Null;
        return ;
    }
    if(Root->Val < Val)
    {
        Insert(Root->R, Val);
        if(Root->R->Pry < Root->Pry)RotateRight(Root);
    }
    else
    {
        Insert(Root->L, Val);
        if(Root->L->Pry < Root->Pry)RotateLeft(Root);
    }
    Update(Root);
}

Event FindNext(TNODE &Root, Event Val)
{
    if(Root == Null)return Event::Null();
    if(Root->Val.floor < Val.floor)return FindNext(Root->R, Val);
    Event Tmp= FindNext(Root->L, Val);
    if(Tmp.idx != -1)return Tmp; else return Root->Val;
}

Event FindPrev(TNODE &Root, Event Val)
{
    if(Root == Null)return Event::Null();
    if(Val.floor < Root->Val.floor)return FindPrev(Root->L, Val);
    Event Tmp= FindPrev(Root->R, Val);
    if(Tmp.idx != -1)return Tmp; else return Root->Val;
}

int GetUp(TNODE &Root, int cur)
{
	if(Root == Null)
		return 0;
	if(Root->Val.floor > cur)
		return Root->R->Size + 1 + GetUp(Root->L, cur);
	return GetUp(Root->R, cur);
}

int GetDown(TNODE &Root, int cur)
{
	if(Root == Null)
		return 0;
	if(Root->Val.floor < cur)
		return Root->L->Size + 1 + GetDown(Root->R, cur);
	return GetDown(Root->L, cur);
}

void Delete(TNODE &Root, Event Val)
{
	if(Root == Null)
	{
		printf("Delete Failed.\n");
		exit(0);
	}
	if(Root->Val == Val)
	{
		if(Root->L == Null && Root->R == Null)
		{
			free(Root);Root= Null; return ;
		}
		if(Root->L->Pry < Root->R->Pry)
		{
			RotateLeft(Root);
			Delete(Root->R, Val);
		}else
		{
			RotateRight(Root);
			Delete(Root->L, Val);
		}
		Update(Root);
		return ;
	}
	if(Val < Root->Val)Delete(Root->L, Val);else Delete(Root->R, Val);
	Update(Root);
}

struct Passenger
{
	int t, s, f, idx;
	static bool Compare(Passenger a, Passenger b)
	{
		return a.t < b.t;
	}
}a[100005];

int64 ans[100005];

int n, m;
int64 tick;
int step;

void Handle(Event x)
{
	if(x.target == -1)
	{
		//printf("Arrived : tick=%I64d, floor=%d, idx=%d\n", tick, x.floor, x.idx);
		++ step;
		ans[x.idx] = tick;
		Delete(Head, x);
	}else
	{
		//printf("Entered : tick=%I64d, floor=%d, target=%d, idx=%d\n", tick, x.floor, x.target, x.idx);
		Delete(Head, x);
		Insert(Head, Event(x.target, x.idx, -1));
	}
}

int main()
{
	Null= new(NODE);
	Null->Val.idx = -1;
    Null->Size= 0;
    Null->Pry= 0x7fffffff;
    Head= Null;

	scanf("%d%d", &n, &m);
	for(int i(1); i <= n; ++ i)
	{
		scanf("%d%d%d", &a[i].t, &a[i].s, &a[i].f);
		a[i].idx = i;
	}
	std::sort(a + 1, a + 1 + n, Passenger::Compare);
	int pos = 1;

	Event cur;
	cur.floor = 1;
	while(step < n)
	{
		Event Tmp;
		while((Tmp = FindNext(Head, cur)).idx != -1)
		{
			if(Tmp.floor != cur.floor)
				break;
			Handle(Tmp);
		}
		if(step == n)
			break;
		if(Head->Size == 0)
		{
			tick = a[pos].t;
			for(; pos <= n && a[pos].t == tick; ++ pos)
			{
				//printf("Insert : tick=%I64d, cur=%d, floor=%d, idx=%d\n", tick, cur.floor, a[pos].s, a[pos].idx);
				Insert(Head, Event(a[pos].s, a[pos].idx, a[pos].f));
			}
		}else
		{
			int64 nextTime = tick;
			int Up = GetUp(Head, cur.floor), Down = GetDown(Head, cur.floor);
			Event Next = FindNext(Head, cur), Prev = FindPrev(Head, cur);
			if(Up >= Down)
				nextTime += Next.floor - cur.floor;
			else
				nextTime += cur.floor - Prev.floor;
			if(pos <= n && a[pos].t <= nextTime)
			{
				int64 old = tick;
				tick = a[pos].t;
				for(; pos <= n && a[pos].t == tick; ++ pos)
				{
					//printf("Insert : tick=%I64d, cur=%d, floor=%d, idx=%d\n", tick, cur.floor, a[pos].s, a[pos].idx);
					Insert(Head, Event(a[pos].s, a[pos].idx, a[pos].f));
