                } else {
                  low = mid + 1;
                }
              }
              diff_cost -= (int)aPath[dA[last_other]].size() - bans;
            }
          }
          ndp[side ^ 1][i] = min(ndp[side ^ 1][i], base + diff_cost);
        }
      }
    }
    memcpy(dp, ndp, sizeof(dp));
  }
