bool check() {
    if (cur.empty()) return true;
    int j = 0;
    int sz = (int) cur.size();
    int cnt = 0;
    for (auto &x: s) {
        if (x == cur[j]) {
            j++;
            if (j == sz) {
                cnt++;
                j = 0;
            }
        }
    }
    if (cnt >= 2) {
        ans = max(ans, cnt * sz);
        return true;
    }
    return false;
}

void chkmx(int &x, int y) { x = max(x, y); }

void relax(string x, string y, string z) {
    int nx = x.size();
    int ny = y.size();
    int nz = z.size();
    vector<vector<vector<int>>> dp(nx + 1, vector<vector<int>>(ny + 1, vector<int>(nz + 1)));
    int res = 0;
    for (int i = 0; i <= nx; ++i) {
        for (int j = 0; j <= ny; ++j) {
            for (int e = 0; e <= nz; ++e) {
                if (i + 1 <= nx) chkmx(dp[i + 1][j][e], dp[i][j][e]);
                if (j + 1 <= ny) chkmx(dp[i][j + 1][e], dp[i][j][e]);
                if (e + 1 <= nz) chkmx(dp[i][j][e + 1], dp[i][j][e]);
                if (i < nx && j < ny && e < nz && x[i] == y[j] && x[i] == z[e])
                    chkmx(dp[i + 1][j + 1][e + 1], dp[i][j][e] + 1);
                chkmx(res, dp[i][j][e]);
            }
        }
    }
    ans = max(ans, res * 3);
}


void relax(string x, string y) {
    int nx = x.size();
    int ny = y.size();
    vector<vector<int>> dp(nx + 1, vector<int>(ny + 1));
    int res = 0;
    for (int i = 0; i <= nx; ++i) {
        for (int j = 0; j <= ny; ++j) {
            if (i + 1 <= nx) chkmx(dp[i + 1][j], dp[i][j]);
            if (j + 1 <= ny) chkmx(dp[i][j + 1], dp[i][j]);
            if (i < nx && j < ny && x[i] == y[j])
                chkmx(dp[i + 1][j + 1], dp[i][j] + 1);
            chkmx(res, dp[i][j]);
        }
    }
    ans = max(ans, res * 2);
}

