                if (mask & (1 << j)) cur.push_back(s[i + j]);
            }
            check();
            cur.clear();
        }
    }
    // 3
    for (int l = K + 1; l < n; ++l) {
        for (int r = l + K + 1; r + K < n; ++r) {
            relax(s.substr(0, l), s.substr(l, r - l), s.substr(r, n));
        }
        relax(s.substr(0, l), s.substr(l, n));
    }
